import React, { useRef, useEffect, useState } from 'react';
import { Alert, Container, Row, Col, Card, CardTitle, CardHeader, CardBody, CardImg , Button, Modal, ModalBody, ModalHeader} from "shards-react";
import { ListGroup, ListGroupItem, ListGroupItemHeading } from "shards-react";

import mapboxgl from 'mapbox-gl';



mapboxgl.accessToken = 'pk.eyJ1IjoiaGl0ZXNoZGV2IiwiYSI6ImNrNmgwdHB4bTI0aHYzbnBzNGIwdG43eTMifQ.A-CLxYkVMOqQA4YE-J0Q1w';


const Map = () => 
{
  const mapContainer = useRef();
  const [lng, setLng] = useState(-70.9);
  const [lat, setLat] = useState(42.35);
  const [zoom, setZoom] = useState(3);

  useEffect(() => {
  const map = new mapboxgl.Map({
  container: mapContainer.current,
  style: 'mapbox://styles/hiteshdev/ckarasopf0xrq1imtoe8dtxz1',
  center: [lng, lat],
  zoom: zoom
  });

  map.on('move', () => {
  setLng(map.getCenter().lng.toFixed(4));
  setLat(map.getCenter().lat.toFixed(4));
  setZoom(map.getZoom().toFixed(12));
  });

  map.on('load', function () {
    map.addSource('maine', {
    'type': 'geojson',
    'data': {
    'type': 'Feature',
    'geometry': {
    'type': 'Polygon',
    'coordinates': [
    [
    [-67.13734351262877, 45.137451890638886],
    [-66.96466, 44.8097],
    [-68.03252, 44.3252],
    [-69.06, 43.98],
    [-70.11617, 43.68405],
    [-70.64573401557249, 43.090083319667144],
    [-70.75102474636725, 43.08003225358635],
    [-70.79761105007827, 43.21973948828747],
    [-70.98176001655037, 43.36789581966826],
    [-70.94416541205806, 43.46633942318431],
    [-71.08482, 45.3052400000002],
    [-70.6600225491012, 45.46022288673396],
    [-70.30495378282376, 45.914794623389355],
    [-70.00014034695016, 46.69317088478567],
    [-69.23708614772835, 47.44777598732787],
    [-68.90478084987546, 47.184794623394396],
    [-68.23430497910454, 47.35462921812177],
    [-67.79035274928509, 47.066248887716995],
    [-67.79141211614706, 45.702585354182816],
    [-67.13734351262877, 45.137451890638886]
    ],
    []


    ]
    }
    }
    });
    map.addLayer({
    'id': 'maine',
    'type': 'fill',
    'source': 'maine',
    'layout': {},
    'paint': {
    'fill-color': '#088',
    'fill-opacity': 0.8
    }
    });
    });

  return () => map.remove();
  }, []);

  return (
  <div className="embed-responsive embed-responsive-16by9">
  <div className="sidebar">
  Longitude: {lng} | Latitude: {lat} | Zoom: {zoom}
  </div>
  <div className="map-container " ref={mapContainer} />
  </div>
  );
  };



class Mapbox extends React.Component
{


  constructor(props) {
    super(props);


  }




  render() 
  {


    return (
      <Container>
      <Row style={{ paddingTop: "30px" }}>
<Col>

<h5>Mapbox </h5>
<hr className="hr-2"></hr>

</Col>

 </Row>

 <Row style={{ paddingTop: "30px" }}>
<Col>

<Map></Map>

</Col>

 </Row>


       </Container>
    )       











  }
}

export default Mapbox;
