import React, { useRef, useEffect, useState } from 'react';

import mapboxgl from 'mapbox-gl';



mapboxgl.accessToken = 'pk.eyJ1IjoiaGl0ZXNoZGV2IiwiYSI6ImNrNmgwdHB4bTI0aHYzbnBzNGIwdG43eTMifQ.A-CLxYkVMOqQA4YE-J0Q1w';


const Map = () => 
{
  const mapContainer = useRef();
  const [lng, setLng] = useState(-70.9);
  const [lat, setLat] = useState(42.35);
  const [zoom, setZoom] = useState(9);

  useEffect(() => {
  const map = new mapboxgl.Map({
  container: mapContainer.current,
  style: 'mapbox://styles/mapbox/streets-v11',
  center: [lng, lat],
  zoom: zoom
  });

  map.on('move', () => {
  setLng(map.getCenter().lng.toFixed(4));
  setLat(map.getCenter().lat.toFixed(4));
  setZoom(map.getZoom().toFixed(2));
  });

  return () => map.remove();
  }, []);

  return (
  <div>
  <div className="sidebar">
  Longitude: {lng} | Latitude: {lat} | Zoom: {zoom}
  </div>
  <div className="map-container" ref={mapContainer} />
  </div>
  );
  };



class Mapbox extends React.Component
{







  render() 
  {


    return (
      <Container>
      <Row style={{ paddingTop: "30px" }}>
<Col>

<h5>Contentful Headless CMS</h5>
<hr className="hr-2"></hr>

</Col>

 </Row>

 <Row style={{ paddingTop: "30px" }}>
<Col>

<Card>
      <CardBody>
        <CardTitle>You are on a live demo </CardTitle>




        <p>This demo portal is built on Contentful Headless CMS end-to-end. </p>



      <ListGroup>
      <ListGroupItem className="active" >Components used to built this portal</ListGroupItem>
      <ListGroupItem>Contentful CMS - GraphQL API</ListGroupItem>
      <ListGroupItem>React JS</ListGroupItem>
      <ListGroupItem>Bootstrap 4</ListGroupItem>
      <ListGroupItem>Shards React  </ListGroupItem>


    </ListGroup>
      </CardBody>
    </Card>

</Col>

 </Row>


       </Container>
    )       











  }
}

export default Mapbox;
