import React from "react";
import { Alert, Container, Row, Col, Card, CardTitle, CardHeader, CardBody, CardImg , Button, Modal, ModalBody, ModalHeader} from "shards-react";
import { ListGroup, ListGroupItem, ListGroupItemHeading } from "shards-react";
import { PAGES } from "../config"

import Services from '../services/services'
import Articles from '../articles/articles'



class Page extends React.Component
{


  state = {
    pageid : null,
    page : null
  }

  // Authenticate the request

  componentDidMount() 
  {
    const id = this.props.match.params.id;
    switch(id)
    {
      case "product":
      this.setState({pageid: this.props.match.params.id });
      this.setState({pageTitle: PAGES.id });

    }
  }



  render() {

    let page; 
    if(this.state.page =="product")
    {
      page = <Services></Services>
    }
    else
    {
      page = <Articles></Articles>
    }


    return (
      <Container>
      <Row style={{ paddingTop: "30px" }}>
<Col>

<h5>{PAGES.PRODUCT}</h5>
<hr className="hr-2"></hr>


</Col>

{page}

 </Row>


       </Container>
    )       





  }
}

export default Page;
