import React from "react";
import { Alert, Container, Row, Col, Card, CardTitle, CardHeader, CardBody, CardImg , Button, Modal, ModalBody, ModalHeader} from "shards-react";
import { Form, FormInput, FormGroup } from "shards-react";
import { FaSkype, FaSlackHash, FaWhatsapp, FaSms, FaMailBulk, FaSlack, FaPhone, FaArrowRight } from 'react-icons/fa';
import { ListGroup, ListGroupItem, ListGroupItemHeading } from "shards-react";

import { useState, useEffect } from "react";
import { appConfig } from "../config"
import axios from 'axios';




class Home extends React.Component
{


  state = {
    articles: []
  }

  // Authenticate the request

  componentDidMount() 
  {
    const API = appConfig.graphQLHost+"/"+appConfig.space_id+"/"
    const headers = {
      'Content-Type': 'application/json',
      'Authorization': 'Bearer '+appConfig.access_token
    }
    axios.post(API, 
      {
        query : `{
          articleCollection(limit: 10) {
            items
            {
              title
              body
              image {fileName url}


            }
          }
        }`

    }
    , {  headers : headers  })
      .then(res => {

        this.setState({ articles: res.data.data.articleCollection.items })
        console.log("RES --> ",res.data.data.articleCollection.items )

      }).catch((error) => {
        console.error(error)
      })
  }



  render() 
  {




    let columns=[];
    this.state.articles.forEach((item,idx) => {



        columns.push(


          <li className="list-group-item">
          <div className="media">
            <div className="media-left">
              <a className="avatar avatar-online" href="javascript:void(0)">
                <img src="{item.image.url}" alt="..."/>
                <i></i>
              </a>
            </div>
            <div className="media-body">
              <small className="text-muted pull-right">Just now</small>
              <h4 className="media-heading">{item.title}</h4>
              <div>Lorem ipsum Veniam aliquip culpa laboris minim tempor labore
                commodo officia veniam non in in in.</div>
            </div>
          </div>
        </li>



        )

        // force wrap to next row every 4 columns

    })


    return (
        <Container style={{ paddingTop: "10px" }}>

   <section className="bgimage">
  <div className="container-fluid">
    <div className="row">
      <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12 bgimage-text ">
      <div className ="container-banner">
       <div className="animation">
          <p className="text-center">Spark Demo Portal</p> 
       </div>
       <h5>
     Technology, Demos and Proof of Concepts
    </h5>

     </div>


      </div>

    </div>
  </div>
</section>



      <div className="panel">

        <div className="panel-body">

          <div className="clearfix"></div>
          <hr className="margin-bottom-10" ></hr>
          <ul className="list-group list-group-dividered list-group-full">

            {columns}



          </ul>

        </div>
      </div>







           <Row style={{ paddingTop: "30px" }}>


        </Row>

        </Container>

    )       





  }
}

export default Home;
