import React from "react";
import { Alert, Container, Row, Col, Card, CardHeader, CardBody, CardImg , Button, Modal, ModalBody, ModalHeader} from "shards-react";

class Iniative extends React.Component
{

  constructor(props) {
    super(props);
    this.state = { open: false };
    this.toggle = this.toggle.bind(this);
  }

  toggle() {
    this.setState({
      open: !this.state.open
    });
  }

  render() {
    const { open } = this.state;


    return(
        <Container>

      <Row style={{ paddingTop: "10px" }}>

        <Col sm={{ size: 8, order: 2, offset: 2 }}>
          <Card> 
            <CardHeader><strong>Cyber Security Course for Beginners - Level 01 </strong></CardHeader>
            <CardImg top src="https://cdn.pixabay.com/photo/2015/07/17/22/43/student-849825_960_720.jpg" />
            <CardBody>
              <p>You've heard this message repeatedly — cyber security is everyone’s responsibility. But is everyone in your organization cyber security responsible? If there are cracks in the defences, you need to learn how to protect and defend against cyber threats with our cyber security training and certification programs.

</p>
              <Button onClick={this.toggle}> Start Program &rarr;</Button>

            </CardBody>
          </Card>
        </Col>

      </Row>

      <Modal open={open} toggle={this.toggle}>
          <ModalHeader>ConsiderIT</ModalHeader>
          <ModalBody> 
          <Row>
            <Col><Button>Log In</Button></Col>
          </Row>
          <Row style={{ paddingTop: "5px" }}> 
            <Col>
            <HR></HR>
            <p>New to the platform ? </p></Col>
          </Row>
          </ModalBody>
        </Modal>
    </Container>

    )
  }
}

export default Iniative;
